catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    //Change choices for E-Voucher Brand and E-Voucher amount fields.

    var voucherType = newValue;
    g_form.clearOptions('e_voucher_brand');
    g_form.clearOptions('e_voucher_amount');
    switch (voucherType) {
        case 'e_commerce':
            g_form.addOption('e_voucher_brand', 'flipkard', 'Flipkart');
            g_form.addOption('e_voucher_brand', 'amazon', 'Amazon');
            g_form.addOption('e_voucher_brand', 'myntra', 'Myntra');

            g_form.addOption('e_voucher_amount', '200', 'Rs.200');
            g_form.addOption('e_voucher_amount', '250', 'Rs.250');
            g_form.addOption('e_voucher_amount', '500', 'Rs.500');

            break;

        case 'shopping':
            g_form.addOption('e_voucher_brand', 'lifestyle', 'Lifestyle');
            g_form.addOption('e_voucher_brand', 'allen_solly', 'Allen Solly');

            g_form.addOption('e_voucher_amount', '400', 'Rs.400');
            g_form.addOption('e_voucher_amount', '750', 'Rs.750');

            break;

        case 'sports_wear':
            g_form.addOption('e_voucher_brand', 'puma', 'Puma');
            g_form.addOption('e_voucher_brand', 'adidas', 'Adidas');
            g_form.addOption('e_voucher_brand', 'nike', 'Nike');

            g_form.addOption('e_voucher_amount', '250', 'Rs.250');
            g_form.addOption('e_voucher_amount', '500', 'Rs.500');
            g_form.addOption('e_voucher_amount', '750', 'Rs.750');

            break;

        case 'food_delivery':
            g_form.addOption('e_voucher_brand', 'zomato', 'Zomato');
            g_form.addOption('e_voucher_brand', 'swiggy', 'Swiggy');

            g_form.addOption('e_voucher_amount', '100', 'Rs.100');
            g_form.addOption('e_voucher_amount', '200', 'Rs.200');
            g_form.addOption('e_voucher_amount', '400', 'Rs.400');

    }

    g_form.addOption('e_voucher_amount', '1000', 'Rs.1000');
}
