sys_script_include.script:
var e_voucherUtils = Class.create();
e_voucherUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getProjectAccount: function() {
        var returnValue;
        var userID = this.getParameter('sysparm_userID');
        if (!gs.nil(userID)) {
            var userGR = new GlideRecord('x_1083671_corpgift_employee');
            if (userGR.get(userID)) {
                returnValue = userGR.project_account;
            }
        }
        return returnValue;
    }, type: 'e_voucherUtils'
});
